import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import '../../App.css';
import axios from 'axios';
import NavigationBar from '../NavigationBar'

class CreateHackathon extends Component {
  constructor() {
    super();
    this.state = {
      name: '',
      description: '',
      reviews: [],
      img: ''
    };
  }

  onChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  onSubmit = e => {
    e.preventDefault();

    const data = {
      name: this.state.name,
      description: this.state.description,
      reviews: this.state.reviews,
      img: this.state.img
    };

    axios
      .post('http://localhost:8082/api/hackathons', data)
      .then(res => {
        this.setState({
          name: '',
          description: '',
          reviews: [],
          img: ''
        })
        this.props.history.push('/');
      })
      .catch(err => {
        console.log("Error in CreateHackathon!");
      })
  };

  render() {
    return (
      <div>
      <NavigationBar/>
      <div className="CreateHackathon">
        <div className="container">
          <div className="row">
            <div className="col-md-8 m-auto">
              <br />
              <Link to="/" className="btn btn-outline-warning float-left">
                  Show Hackathon List
              </Link>
            </div>
            <div className="col-md-8 m-auto">
              <h1 className="display-4 text-center">Add Hackathon</h1>
              <p className="lead text-center">
                  Add a new hackathon to the database to recieve reviews!
              </p>

              <form noValidate onSubmit={this.onSubmit}>

                <div className='form-group'>
                  <input
                    type='text'
                    placeholder='Hackathon Name'
                    name='name'
                    className='form-control'
                    value={this.state.name}
                    onChange={this.onChange}
                  />
                </div>
                <br />

                <div className='form-group'>
                  <input
                    type='text'
                    placeholder='Hackathon Description'
                    name='description'
                    className='form-control'
                    value={this.state.description}
                    onChange={this.onChange}
                  />
                </div>
                <br />

                <div className='form-group'>
                  <input
                    type='text'
                    placeholder='Image'
                    name='img'
                    className='form-control'
                    value={this.state.img}
                    onChange={this.onChange}
                  />
                </div>

                {/* <div className='form-group'>
                  <div className="custom-file">
                    <input
                      type="file"
                      accept=".png, .jpg, .jpeg"
                      name="img"
                      className="custom-file-input"
                      value={this.state.img}
                      onChange={this.onChange}
                    />
                    <label className="custom-file-label" htmlFor="inputGroupFile01">
                      {this.state.img}
                    </label>
                  </div>
                </div> */}

                <input
                    type="submit"
                    className="btn btn-outline-warning btn-block mt-4"
                />
              </form>
          </div>
          </div>
        </div>
      </div>
      </div>
    );
  }
}

export default CreateHackathon;
