import React, { Component } from "react";
import "../App.css";
import axios from "axios";
// import { Link } from 'react-router-dom';
import HackathonCard from "../components/HackathonCard";
import { Container, Header } from "semantic-ui-react";

class ShowHackathonList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      hackathons: [],
    };
  }

  componentDidMount() {
    axios
      .get("http://localhost:8082/api/hackathons")
      .then((res) => {
        this.setState({
          hackathons: res.data,
        });
      })
      .catch((err) => {
        console.log("Error from ShowHackathonList");
      });
  }

  render() {
    const hackathons = this.state.hackathons;
    console.log("PrintHackathon: " + hackathons);
    let hackathonList;

    if (!hackathons) {
      hackathonList = "there is no hackathon record!";
    } else {
      hackathonList = hackathons.map((hackathon, k) => (
        <HackathonCard hackathon={hackathon} key={k} />
      ));
    }

    return (
      <Container>
        <Header 
        as="h1"
        style={{ padding: "2em 0em 1em 0em" }}>
          All Hackathons
          <Header.Subheader>
            View all hackathons in our database. Don't see yours? 
            <a href="/new-hackathon"> Add it!</a>
          </Header.Subheader>
        </Header>
        {hackathonList}
      </Container>
    );
  }
}

export default ShowHackathonList;
