import React, { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "../../App.css";
import { Header, Container, Form, Button, Divider } from "semantic-ui-react";

class NewReview extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      description: "",
      reviews: [],
      reviewTitle: "",
      reviewDescription: "",
      reviewRating: 3,
      reviewTags: [],
    };
    this.changeRating = this.changeRating.bind(this);
  }

  componentDidMount() {
    axios
      .get("http://localhost:8082/api/hackathons/" + this.props.match.params.id)
      .then((res) => {
        this.setState({
          id: res.data.id,
          name: res.data.name,
          description: res.data.description,
          reviews: res.data.reviews,
        });
      })
      .catch((err) => {
        console.log("Error from NewReview");
      });
  }

  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  selectedItem = (e, data) => {
    this.setState({
      reviewTags: data.value
    });
  }

  onSubmit = (e) => {
    e.preventDefault();

    const reviewTitle = this.state.reviewTitle;
    const reviewDescription = this.state.reviewDescription;
    const reviewRating = this.state.reviewRating;

    const obj = {
      title: reviewTitle,
      description: reviewDescription,
      rating: reviewRating,
    };

    const data = {
      name: this.state.name,
      description: this.state.description,
      reviews: [...this.state.reviews, obj],
    };

    axios
      .put(
        "http://localhost:8082/api/hackathons/" + this.props.match.params.id,
        data
      )
      .then((res) => {
        this.props.history.push("/hackathon/" + this.props.match.params.id);
      })
      .catch((err) => {
        console.log("Error in NewReview!");
      });
  };

  changeRating(val) {
    this.setState({
      reviewRating: val,
    });
  }

  render() {
    const tagOptions = [
      { key: "competitive", text: "Competitive", value: "competitive" },
      { key: "diverse", text: "Diverse", value: "diverse" },
      { key: "fun", text: "Fun", value: "fun" },
      { key: "organized", text: "Organized", value: "organized" },
      { key: "stressful", text: "Stressful", value: "stressful" },
      { key: "judging", text: "Judging", value: "judging"},
      { key: "mentors", text: "Mentors", value: "mentors" },
      { key: "panels", text: "Panels", value: "panels" },
      { key: "recruiters", text: "Recruiters", value: "recruiters" },
      { key: "resources", text: "Resources", value: "resources" },
      { key: "swag", text: "Swag", value: "swag" },
      { key: "workshops", text: "Workshops", value: "workshops" },
    ];

    return (
      <div>
        <Container textAlign="center">
          <Header as="h1" style={{ padding: "1.5em 0em 1.5em" }}>
          Add a Review - {this.state.name}
            <Header.Subheader>
              Before submitting your review, please remember:<br/><br/>

              Be honest. This review should be based on <u>your</u> own experience.<br/>
              Be respectful. Do not include profanity or derogatory names in your review.<br/>
              When criticizing, be sure that it is <u>constructive</u>, not destructive.<br/>
              <br/>
              RMH reserves the right to remove any reviews that violate any of these terms.
            </Header.Subheader>
          </Header>
        </Container>

        <Container>
          <Form onSubmit={this.onSubmit}>
            <Header as="h3">Basic Info</Header>

            <Form.Radio>
              Rating
            </Form.Radio>

            <Form.TextArea
              fluid
              required
              label="Experience Details"
              placeholder="Tell us more about your experience..."
              name="reviewDescription"
              value={this.state.reviewDescription}
              onChange={this.onChange}
            />

            <Header as="h3">Additional Info</Header>
            <Form.Select
              fluid
              multiple
              search
              label="Adjectives to describe this hackathon or things that stood out to me"
              placeholder="Tags"
              options={tagOptions}
              name="reviewTags"
              value={this.state.reviewTags}
              onChange={this.selectedItem}
            />

            <Divider hidden />
            <Button fluid primary type="submit">
              Add Review
            </Button>
          </Form>
        </Container>
      </div>



      //         <div className="form-group">
      //           <label htmlFor="reviewRating">Rating</label>

      //           <br></br>
      //           {/* <ToggleButtonGroup
      //         type="radio"
      //         name="reviewRating"
      //         onChange={this.changeRating}
      //         defaultValue={3}
      //       >
      //         <ToggleButton value={1}>1</ToggleButton>
      //         <ToggleButton value={2}>2</ToggleButton>
      //         <ToggleButton value={3}>3</ToggleButton>
      //         <ToggleButton value={4}>4</ToggleButton>
      //         <ToggleButton value={5}>5</ToggleButton>
      //       </ToggleButtonGroup> */}
      //         </div>

      //         <br />

      //         <button
      //           type="submit"
      //           className="btn btn-primary btn-lg btn-block"
      //         >
      //           Submit Review
      //         </button>
      //       </form>
      //     </div>
      //   </div>
      // </div>
    );
  }
}

export default NewReview;
