import React, { Component } from "react";
import "../../App.css";
import axios from "axios";
import { Link } from 'react-router-dom';
import HackathonCard from "./HackathonCard";
import { Container, Header, Input, Message, Grid } from "semantic-ui-react";
import SearchBar from "../SearchBar";

class SearchHackathons extends Component {
  constructor(props) {
    super(props);
    this.state = {
      value: "",
      hackathons: [],
    };
  }

  // componentDidMount() {
  //   axios
  //     .get("http://localhost:8082/api/hackathons")
  //     .then((res) => {
  //       this.setState({
  //         hackathons: res.data,
  //       });
  //     })
  //     .catch((err) => {
  //       console.log("Error getting all hackathons in SearchHackathons");
  //     });
  // }

  search = async (val) => {
    const res = await axios(
      `http://localhost:8082/api/hackathons/search?q=${val}`
    );
    const hackathons = await res.data;

    this.setState({ hackathons });
  };

  onChangeHandler = async (e) => {
    this.search(e.target.value);
    this.setState({ value: e.target.value });
  };

  get renderHackathons() {
    let hackathonsList = (<div>
    <Message>
      <Grid textAlign="center">
        <Grid.Row><br/></Grid.Row>
        <Grid.Row>
          <Header as="h2">No Hackathons Found</Header>
        </Grid.Row>
        <Grid.Row>
          <p>
            No hackathons were found that matched this query. Try searching for something else or {" "}
            <Link to={`/new-hackathon/`}>add a hackathon</Link>!
          </p>
        </Grid.Row>
        <Grid.Row><br/></Grid.Row>
      </Grid>
    </Message>
    </div>)

    if (this.state.hackathons) {
      hackathonsList = this.state.hackathons.map((hackathon, k) => (
        <HackathonCard hackathon={hackathon} key={k} />
      ));
    }
    console.log("LIST: " + hackathonsList)
    return hackathonsList;
  }

  render() {
    // const hackathons = this.state.hackathons;
    // const query = this.state.query;
    return (
      <div>
        <Container textAlign="center">
          <Header as="h1" style={{ padding: "1.5em 0em 1.5em" }}>
            Search Hackathons
            <Header.Subheader>
              Search through hackathons in our database. Don't see yours?
              <a href="/new-hackathon"> Add it!</a>
            </Header.Subheader>
          </Header>
          {/* <SearchBar /> */}
          <Input
            fluid
            value={this.state.value}
            onChange={(e) => this.onChangeHandler(e)}
            placeholder="Type something to search"
          />

          {/* <Input
            fluid
            icon="search"
            size="large"
            type="text"
            placeholder="Search hackathons..."
            onChange={this.onChange(query)}
            value={query}
          /> */}
        </Container>

        <Container style={{ padding: "3em 0 0" }}>
          {/* <Header as="h2">Displaying search results for: {}</Header> */}
          {this.renderHackathons}
        </Container>
      </div>
    );
  }
}

export default SearchHackathons;
