import React from "react";
import { Label, Form, Input, Button } from "semantic-ui-react";

const SearchBar = (query) => (
  <Form action="/hackathons/search" method="get">
    <Input
      fluid
      icon="search"
      size="large"
      type="text"
      id="header-search"
      placeholder="Search hackathons..."
      name="q"
    />
    <input type="hidden" value={query} />
  </Form>
);

