import React, { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "../../App.css";
import { Header, Container, Form, Button, Divider } from "semantic-ui-react";

class NewReview extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      description: "",
      reviews: [],
      reviewTitle: "",
      reviewDescription: "",
      reviewRating: 3,
      reviewTags: [],
    };
    this.changeRating = this.changeRating.bind(this);
  }

  componentDidMount() {
    axios
      .get("http://localhost:8082/api/hackathons/" + this.props.match.params.id)
      .then((res) => {
        this.setState({
          id: res.data.id,
          name: res.data.name,
          description: res.data.description,
          reviews: res.data.reviews,
        });
      })
      .catch((err) => {
        console.log("Error from NewReview");
      });
  }

  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  onSubmit = (e) => {
    e.preventDefault();

    const reviewTitle = this.state.reviewTitle;
    const reviewDescription = this.state.reviewDescription;
    const reviewRating = this.state.reviewRating;

    const obj = {
      title: reviewTitle,
      description: reviewDescription,
      rating: reviewRating,
    };

    const data = {
      name: this.state.name,
      description: this.state.description,
      reviews: [...this.state.reviews, obj],
    };

    axios
      .put(
        "http://localhost:8082/api/hackathons/" + this.props.match.params.id,
        data
      )
      .then((res) => {
        this.props.history.push("/hackathon/" + this.props.match.params.id);
      })
      .catch((err) => {
        console.log("Error in NewReview!");
      });
  };

  changeRating(val) {
    this.setState({
      reviewRating: val,
    });
  }

  render() {
    const tagOptions = [
      { key: "competitive", text: "Competitive", value: "competitive" },
      { key: "diverse", text: "Diverse", value: "diverse" },
      { key: "fun", text: "Fun", value: "fun" },
      { key: "organized", text: "Organized", value: "organized" },
      { key: "stressful", text: "Stressful", value: "stressful" },
      { key: "judging", value: "judging" },
      { key: "mentors", text: "Mentors", value: "mentors" },
      { key: "panels", text: "Panels", value: "panels" },
      { key: "recruiters", text: "Recruiters", value: "recruiters" },
      { key: "resources", text: "Resources", value: "resources" },
      { key: "swag", text: "Swag", value: "swag" },
      { key: "workshops", text: "Workshops", value: "workshops" },
    ];

    return (
      <div>
        <Container textAlign="center">
          <Header as="h1" style={{ padding: "1.5em 0em 1.5em" }}>
            Add a Review
            <Header.Subheader>
              Write a review for {this.state.name}! Please remember:
              <ul>
              <li>Be respectful.</li>
              <li>Only provide constructive critism.</li>
              <li>Any hate comments will be taken down.</li>
              </ul>
            </Header.Subheader>
          </Header>
        </Container>

        <Container>
          <Form onSubmit={this.onSubmit}>
            <Header as="h3">Basic Info</Header>

            <Form.Input
              fluid
              required
              label="Name"
              placeholder="Hackathon name"
              name="name"
              value={this.state.name}
              onChange={this.onChange}
            />
            <Form.TextArea
              fluid
              required
              label="Description"
              placeholder="Tell us more about this hackathon..."
              name="description"
              value={this.state.description}
              onChange={this.onChange}
            />

            <Header as="h3">Additional Info</Header>
            <Form.Input
              fluid
              label="Website URL"
              placeholder="https://www.ratemyhackathon.online"
              name="url"
              value={this.state.url}
              onChange={this.onChange}
            />

            <Form.Select
              fluid
              multiple
              search
              label="Themes"
              placeholder="Hackathon themes"
              options={tagOptions}
              name="themes"
              value={this.state.themes}
              onChange={this.selectedItem}
            />

            <Divider hidden />
            <Button fluid primary type="submit">
              Add Hackathon
            </Button>
          </Form>
        </Container>
      </div>

      // <div className="NewHackathon">
