import React from "react";
import "../../App.css";
import { Card } from "semantic-ui-react";

const ReviewCard = (props) => {
  const review = props.review;

  var dateString = new Date(review.date_updated).toISOString()
  // dateString = dateString.substr(dateString.indexOf(' ')+1);

  return (
    <Card centered fluid>
      <Card.Content header>
        <span className="left">{review.title}</span>
        <span className="right">{review.rating} / 5</span>
      </Card.Content>
      <Card.Content description>
        {review.description}
        <br/>
        <br/>
        <span className="right">Written on {dateString}</span>
      </Card.Content>
    </Card>
  );
};

export default ReviewCard;
