import React, { Component } from "react";
import "../../App.css";
import axios from "axios";
// import { Link } from 'react-router-dom';
import HackathonCard from "./HackathonCard";
import { Container, Header, Input, Form } from "semantic-ui-react";
import SearchBar from "../SearchBar";

class SearchHackathons extends Component {
  constructor(props) {
    super(props);
    this.state = {
      query: "",
      hackathons: [],
    };
  }

  componentDidMount() {
    axios
      .get("http://localhost:8082/api/hackathons")
      .then((res) => {
        this.setState({
          hackathons: res.data,
        });
      })
      .catch((err) => {
        console.log("Error getting all hackathons in SearchHackathons");
      });
  }

  onChange(q) {
    axios
      .get("http://localhost:8082/api/hackathons/search?q=" + q)
      .then((res) => {
        this.setState({
          query: q,
          hackathons: res.data,
        });
      })
      .catch((err) => {
        console.log("Error searching in SearchHackathons");
      });
  }

  render() {
    const hackathons = this.state.hackathons;
    const query = this.state.query;

    let hackathonList;

    if (!hackathons) {
      hackathonList = "There is no hackathon record!";
    } else {
      hackathonList = hackathons.map((hackathon, k) => (
        <HackathonCard hackathon={hackathon} key={k} />
      ));
    }

    return (
      <div>
        <Container textAlign="center">
          <Header as="h1" style={{ padding: "1.5em 0em 1.5em" }}>
            Search Hackathons
            <Header.Subheader>
              Search through hackathons in our database. Don't see yours?
              <a href="/new-hackathon"> Add it!</a>
            </Header.Subheader>
          </Header>
          {/* <SearchBar /> */}
          <Form action={this.onChange(query)}>
            <Input
              fluid
              icon="search"
              size="large"
              type="text"
              id="header-search"
              placeholder="Search hackathons..."
              name="q"
            />
            <input type="hidden" value={query} />
          </Form>
          {/* <Input
            fluid
            icon="search"
            size="large"
            type="text"
            placeholder="Search hackathons..."
            onChange={this.onChange(query)}
            value={query}
          /> */}
        </Container>

        <Container style={{ padding: "3em 0 0" }}>
          {/* <Header as="h2">Displaying search results for: {}</Header> */}
          {hackathonList}
        </Container>
      </div>
    );
  }
}

export default SearchHackathons;
