import React, { Component } from "react";
import { Link } from "react-router-dom";
import "../../App.css";
import axios from "axios";
import ReviewCard from "../ReviewCard";
import { Header, Container, Form, Button, Divider } from "semantic-ui-react";


class ViewHackathon extends Component {
  constructor(props) {
    super(props);
    this.state = {
      hackathon: {},
    };
  }

  componentDidMount() {
    // console.log("Print id: " + this.props.match.params.id);
    axios
      .get("http://localhost:8082/api/hackathons/" + this.props.match.params.id)
      .then((res) => {
        // console.log("Print-ViewHackathon-API-response: " + res.data);
        this.setState({
          hackathon: res.data,
        });
      })
      .catch((err) => {
        console.log("Error from ViewHackathon");
      });
  }

  onDeleteClick(id) {
    axios
      .delete("http://localhost:8082/api/hackathons/" + id)
      .then((res) => {
        this.props.history.push("/");
      })
      .catch((err) => {
        console.log("Error form ViewHackathon_deleteClick");
      });
  }

  render() {
    const hackathon = this.state.hackathon;

    // get list of reviews, if any
    let reviewList;
    if (hackathon.reviews === undefined) {
      reviewList = "There are no reviews!";
    } else {
      reviewList = hackathon.reviews.map((review, k) => (
        <ReviewCard review={review} key={k} />
      ));
    }

    // let HackathonItem = <div>
    //   <table className="table table-hover table-dark">
    //     <tbody>
    //       <tr>
    //         <th scope="row">1</th>
    //         <td>Name</td>
    //         <td>{ hackathon.name }</td>
    //       </tr>

    //       <tr>
    //         <th scope="row">2</th>
    //         <td>Description</td>
    //         <td>{ hackathon.description }</td>
    //       </tr>

    //       <tr>
    //         <th scope="row">3</th>
    //         <td>Last Updated</td>
    //         <td>{ hackathon.date_updated }</td>
    //       </tr>

    //     </tbody>
    //   </table>
    // </div>

    return (
      <div>
        <Container textAlign="center">
          <Header as="h1" style={{ padding: "1.5em 0em 1.5em" }}>
            { hackathon.name }
            <Header.Subheader>
            { hackathon.description }
            </Header.Subheader>
          </Header>
        </Container>

        <Container>

        </Container>

      <div>
        <div className="ViewHackathon">
          <div className="container">


                {/* <button
                  type="button"
                  className="btn btn-outline-danger btn-lg btn-block"
                  onClick={this.onDeleteClick.bind(this, hackathon._id)}
                >
                  Delete Hackathon
                </button> */}

                {/* <Link
                  to={`/edit-hackathon/${hackathon._id}`}
                  className="btn btn-outline-info btn-lg btn-block"
                >
                  Edit Hackathon
                </Link> */}

            <h1 className="display-4 text-center">Reviews</h1>

            <div>{reviewList}</div>

            <Link
              to={`/new-review/${hackathon._id}`}
              className="btn btn-outline-warning btn-lg btn-block"
            >
              Add a Review
            </Link>
          </div>
        </div>
      </div>
      </div>
    );
  }
}

export default ViewHackathon;
