import React from "react";
import "../../App.css";
import { Card } from "semantic-ui-react";

const ReviewCard = (props) => {
  const review = props.review;

  var dateString = new Date(review.date_updated).toDateString() // turn date into readable string (MMM DD YYYY)
  dateString = dateString.substr(dateString.indexOf(' ')+1); // remove day of week from date

  return (
    <Card centered fluid>
      <Card.Content header>
      <span className="left">{review.rating} / 5</span>
      <span className="right">{dateString}</span>

      </Card.Content>
      <Card.Content description>
        {review.description}
        <br/>
        <br/>
        <span className="left">
        <i class="arrow alternate circle up outline icon"></i>
        <i class="arrow alternate circle down outline icon"></i>
        </span>
        <span className="right">
        <i class="flag outline icon"></i>
        </span>
      </Card.Content>
    </Card>
  );
};

export default ReviewCard;
