import React from "react";
import "../../App.css";
import { Card } from "semantic-ui-react";

const ReviewCard = (props) => {
  const review = props.review;

  var date = review.date_updated.toString();

  return (
    <Card centered fluid>
      <Card.Content header>
        <span className="left">{review.title} {date}</span>
        <span className="right">{review.rating} / 5</span>
      </Card.Content>
      <Card.Content description={review.description}/>
      <Card.Content extra={date} />
    </Card>
  );
};

export default ReviewCard;
